import React, { useState, useEffect } from "react";
import { v1 as uuid } from "uuid";
import PokemonSelect from "./PokemonSelect";
import PokemonCard from "./PokemonCard";
import "./PokeDex.css";
import { useAxios } from "./useAxios";

/* Renders a list of pokemon cards.
 * Can also add a new card at random,
 * or from a dropdown of available pokemon. */
function PokeDex() {
  const [pokemon, setPokemon] = useState([]);

  // Use useAxios hook at the top level
  const [data, fetchData] = useAxios(null); // Initialize with null URL

  const addPokemon = async name => {
    try {
      // Call fetchData with the new URL
      await fetchData(`https://pokeapi.co/api/v2/pokemon/${name}`);
    } catch (error) {
      console.error("Error fetching Pokémon data:", error);
    }
  };

  // Update pokemon state when data changes
  useEffect(() => {
    if (data) {
      setPokemon(pokemon => [...pokemon, { ...data, id: uuid() }]);
    }
  }, [data]);

  return (
    <div className="PokeDex">
      <div className="PokeDex-buttons">
        <h3>Please select your pokemon:</h3>
        <PokemonSelect add={addPokemon} />
      </div>
      <div className="PokeDex-card-area">
        {pokemon.map(data => (
          <PokemonCard
            key={data.id}
            front={data.sprites.front_default}
            back={data.sprites.back_default}
            name={data.name}
            stats={data.stats.map(stat => ({
              value: stat.base_stat,
              name: stat.stat.name
            }))}
          />
        ))}
      </div>
    </div>
  );
}

export default PokeDex;
